import React, { useState } from 'react';
import { useQuery, useMutation, useQueryClient } from 'react-query';
import { Settings, Star, Edit } from 'lucide-react';
import { apiService } from '../services/api';
import { getSubjectColor, getLearningStyleName } from '../utils/helpers';
import LoadingSpinner from '../components/LoadingSpinner';
import toast from 'react-hot-toast';

const ProfilePage = () => {
  const [isEditing, setIsEditing] = useState(false);
  const queryClient = useQueryClient();

  const { data: profile, isLoading } = useQuery('profile', apiService.getProfile);

  const updateProfileMutation = useMutation(apiService.updateProfile, {
    onSuccess: () => {
      queryClient.invalidateQueries('profile');
      setIsEditing(false);
      toast.success('Profile updated successfully!');
    },
    onError: () => {
      toast.error('Failed to update profile');
    }
  });

  if (isLoading) {
    return (
      <div className="flex items-center justify-center py-12">
        <LoadingSpinner size="lg" />
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="text-center">
        <h1 className="text-3xl font-bold text-gray-900 mb-2">Your Profile</h1>
        <p className="text-gray-600">Manage your study preferences and information</p>
      </div>

      <div className="bg-white rounded-xl shadow-sm p-8 max-w-2xl mx-auto">
        <div className="text-center mb-8">
          <img 
            src={profile?.avatar || '/default-avatar.png'} 
            alt="Profile" 
            className="w-24 h-24 rounded-full mx-auto mb-4" 
          />
          <h2 className="text-2xl font-bold text-gray-900">{profile?.name}</h2>
          <p className="text-gray-600">{profile?.email}</p>
        </div>

        {isEditing ? (
          <EditProfileForm
            profile={profile}
            onSave={(data) => updateProfileMutation.mutate(data)}
            onCancel={() => setIsEditing(false)}
            isLoading={updateProfileMutation.isLoading}
          />
        ) : (
          <ProfileView
            profile={profile}
            onEdit={() => setIsEditing(true)}
          />
        )}
      </div>
    </div>
  );
};

const ProfileView = ({ profile, onEdit }) => (
  <div className="space-y-6">
    <div>
      <h3 className="text-lg font-semibold text-gray-900 mb-3">Study Information</h3>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-2">Subjects</label>
          <div className="flex flex-wrap gap-2">
            {profile?.profile?.subjects?.map(subject => (
              <span key={subject} className={`badge ${getSubjectColor(subject)}`}>
                {subject}
              </span>
            ))}
          </div>
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700 mb-2">Learning Style</label>
          <div className="text-gray-900">
            {getLearningStyleName(profile?.profile?.learningStyle)}
          </div>
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700 mb-2">Performance Level</label>
          <div className="flex">
            {[1, 2, 3, 4, 5].map(level => (
              <Star
                key={level}
                className={`w-5 h-5 ${
                  level <= (profile?.profile?.performanceLevel || 0)
                    ? 'text-yellow-400 fill-current'
                    : 'text-gray-300'
                }`}
              />
            ))}
          </div>
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700 mb-2">Availability</label>
          <div className="flex flex-wrap gap-2">
            {profile?.profile?.availability?.map(day => (
              <span key={day} className="badge badge-success">
                {day}
              </span>
            ))}
          </div>
        </div>
      </div>

      <div className="mt-4">
        <label className="block text-sm font-medium text-gray-700 mb-2">Study Goals</label>
        <p className="text-gray-900 bg-gray-50 p-3 rounded-lg">
          {profile?.profile?.goals || 'No goals set'}
        </p>
      </div>
    </div>

    <button
      onClick={onEdit}
      className="w-full btn-primary flex items-center justify-center gap-2"
    >
      <Edit className="w-4 h-4" />
      Edit Profile
    </button>
  </div>
);

const EditProfileForm = ({ profile, onSave, onCancel, isLoading }) => {
  const [formData, setFormData] = useState({
    subjects: profile?.profile?.subjects || [],
    learningStyle: profile?.profile?.learningStyle || 1,
    availability: profile?.profile?.availability || [],
    performanceLevel: profile?.profile?.performanceLevel || 3,
    goals: profile?.profile?.goals || ''
  });

  const subjects = ["Mathematics", "Physics", "Chemistry", "Biology", "Computer Science", "Statistics"];
  const days = ["Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday", "Sunday"];
  const learningStyles = [
    { id: 1, name: "Visual" },
    { id: 2, name: "Auditory" },
    { id: 3, name: "Kinesthetic" },
    { id: 4, name: "Reading/Writing" }
  ];

  const handleSubjectToggle = (subject) => {
    setFormData(prev => ({
      ...prev,
      subjects: prev.subjects.includes(subject)
        ? prev.subjects.filter(s => s !== subject)
        : [...prev.subjects, subject]
    }));
  };

  const handleAvailabilityToggle = (day) => {
    setFormData(prev => ({
      ...prev,
      availability: prev.availability.includes(day)
        ? prev.availability.filter(d => d !== day)
        : [...prev.availability, day]
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSave(formData);
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-6">
      <div>
        <label className="block text-sm font-medium text-gray-700 mb-2">Subjects</label>
        <div className="grid grid-cols-2 gap-2">
          {subjects.map(subject => (
            <button
              key={subject}
              type="button"
              onClick={() => handleSubjectToggle(subject)}
              className={`p-2 rounded border text-sm transition-colors ${
                formData.subjects.includes(subject)
                  ? 'border-primary-500 bg-primary-50 text-primary-700'
                  : 'border-gray-300 hover:border-gray-400'
              }`}
            >
              {subject}
            </button>
          ))}
        </div>
      </div>

      <div>
        <label className="block text-sm font-medium text-gray-700 mb-2">Learning Style</label>
        <div className="space-y-2">
          {learningStyles.map(style => (
            <button
              key={style.id}
              type="button"
              onClick={() => setFormData(prev => ({ ...prev, learningStyle: style.id }))}
              className={`w-full p-3 rounded border text-left transition-colors ${
                formData.learningStyle === style.id
                  ? 'border-primary-500 bg-primary-50'
                  : 'border-gray-300 hover:border-gray-400'
              }`}
            >
              {style.name}
            </button>
          ))}
        </div>
      </div>

      <div>
        <label className="block text-sm font-medium text-gray-700 mb-2">Availability</label>
        <div className="grid grid-cols-4 gap-2">
          {days.map(day => (
            <button
              key={day}
              type="button"
              onClick={() => handleAvailabilityToggle(day)}
              className={`p-2 rounded border text-sm transition-colors ${
                formData.availability.includes(day)
                  ? 'border-primary-500 bg-primary-50 text-primary-700'
                  : 'border-gray-300 hover:border-gray-400'
              }`}
            >
              {day.slice(0, 3)}
            </button>
          ))}
        </div>
      </div>

      <div>
        <label className="block text-sm font-medium text-gray-700 mb-2">Performance Level</label>
        <div className="flex items-center gap-4">
          <span className="text-sm text-gray-600">Beginner</span>
          <div className="flex gap-2">
            {[1, 2, 3, 4, 5].map(level => (
              <button
                key={level}
                type="button"
                onClick={() => setFormData(prev => ({ ...prev, performanceLevel: level }))}
                className={`w-8 h-8 rounded-full border-2 transition-colors ${
                  formData.performanceLevel >= level
                    ? 'border-primary-500 bg-primary-500 text-white'
                    : 'border-gray-300 hover:border-primary-300'
                }`}
              >
                {level}
              </button>
            ))}
          </div>
          <span className="text-sm text-gray-600">Expert</span>
        </div>
      </div>

      <div>
        <label className="block text-sm font-medium text-gray-700 mb-2">Goals</label>
        <textarea
          value={formData.goals}
          onChange={(e) => setFormData(prev => ({ ...prev, goals: e.target.value }))}
          className="input-primary"
          rows={3}
          placeholder="What are your study goals?"
        />
      </div>

      <div className="flex gap-3">
        <button
          type="button"
          onClick={onCancel}
          className="flex-1 btn-secondary"
          disabled={isLoading}
        >
          Cancel
        </button>
        <button
          type="submit"
          disabled={isLoading}
          className="flex-1 btn-primary flex items-center justify-center gap-2"
        >
          {isLoading ? (
            <>
              <LoadingSpinner size="sm" />
              Saving...
            </>
          ) : (
            'Save Changes'
          )}
        </button>
      </div>
    </form>
  );
};

export default ProfilePage;
