import React, { useState, useEffect } from 'react';
import { useAuth } from './contexts/AuthContext';
import { authService } from './services/auth';
import LoginPage from './pages/LoginPage';
import ProfileSetup from './pages/ProfileSetup';
import Layout from './components/Layout';
import LoadingSpinner from './components/LoadingSpinner';
import Dashboard from './pages/Dashboard';
import MatchesPage from './pages/MatchesPage';
import SessionsPage from './pages/SessionsPage';
import MessagesPage from './pages/MessagesPage';
import ProfilePage from './pages/ProfilePage';

const App = () => {
  const { user, isAuthenticated, isLoading, login } = useAuth();
  const [currentPage, setCurrentPage] = useState('dashboard');
  const [profileComplete, setProfileComplete] = useState(false);

  useEffect(() => {
    // Check for Auth0 callback
    const code = authService.getTokenFromUrl();
    if (code) {
      authService.clearUrl();
      login(code);
    }
  }, [login]);

  useEffect(() => {
    if (user) {
      setProfileComplete(user.profileComplete || (user.profile && user.profile.subjects && user.profile.subjects.length > 0));
    }
  }, [user]);

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <LoadingSpinner size="xl" />
      </div>
    );
  }

  if (!isAuthenticated) {
    return <LoginPage />;
  }

  if (!profileComplete) {
    return <ProfileSetup onComplete={() => setProfileComplete(true)} />;
  }

  const renderPage = () => {
    switch (currentPage) {
      case 'dashboard':
        return <Dashboard />;
      case 'matches':
        return <MatchesPage />;
      case 'sessions':
        return <SessionsPage />;
      case 'messages':
        return <MessagesPage />;
      case 'profile':
        return <ProfilePage />;
      default:
        return <Dashboard />;
    }
  };

  return (
    <Layout currentPage={currentPage} setCurrentPage={setCurrentPage}>
      {renderPage()}
    </Layout>
  );
};

export default App;
