import React, { useState } from 'react';
import { useQuery, useMutation, useQueryClient } from 'react-query';
import { Calendar, Clock, Users, Plus, MapPin } from 'lucide-react';
import { apiService } from '../services/api';
import { getSessionStatusColor, formatDateTime } from '../utils/helpers';
import LoadingSpinner from '../components/LoadingSpinner';
import toast from 'react-hot-toast';

const SessionsPage = () => {
  const [showCreateForm, setShowCreateForm] = useState(false);
  const queryClient = useQueryClient();

  const { data: sessions, isLoading } = useQuery('sessions', () => 
    apiService.getSessions({ limit: 20 })
  );

  const createSessionMutation = useMutation(apiService.createSession, {
    onSuccess: () => {
      queryClient.invalidateQueries('sessions');
      setShowCreateForm(false);
      toast.success('Session created successfully!');
    },
    onError: () => {
      toast.error('Failed to create session');
    }
  });

  const joinSessionMutation = useMutation(apiService.joinSession, {
    onSuccess: () => {
      queryClient.invalidateQueries('sessions');
      toast.success('Joined session successfully!');
    },
    onError: () => {
      toast.error('Failed to join session');
    }
  });

  const handleJoinSession = (sessionId) => {
    joinSessionMutation.mutate(sessionId);
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center py-12">
        <LoadingSpinner size="lg" />
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold text-gray-900">Study Sessions</h1>
          <p className="text-gray-600">Manage your scheduled study sessions</p>
        </div>
        <button
          onClick={() => setShowCreateForm(true)}
          className="btn-primary flex items-center gap-2"
        >
          <Plus className="w-4 h-4" />
          Create Session
        </button>
      </div>

      {sessions?.sessions?.length === 0 ? (
        <div className="text-center py-12">
          <Calendar className="w-16 h-16 mx-auto mb-4 text-gray-300" />
          <h3 className="text-lg font-semibold text-gray-900 mb-2">No study sessions yet</h3>
          <p className="text-gray-600 mb-6">Schedule your first session with a study buddy!</p>
          <button
            onClick={() => setShowCreateForm(true)}
            className="btn-primary"
          >
            Create Your First Session
          </button>
        </div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {sessions?.sessions?.map(session => (
            <div key={session.id} className="card p-6">
              <div className="flex items-start justify-between mb-4">
                <div className="flex-1">
                  <h3 className="font-semibold text-gray-900 mb-1">{session.title}</h3>
                  <p className="text-sm text-gray-600">{session.subject}</p>
                </div>
                <span className={`badge ${getSessionStatusColor(session.status)}`}>
                  {session.status}
                </span>
              </div>

              <div className="space-y-2 mb-4">
                <div className="flex items-center gap-2 text-sm text-gray-600">
                  <Calendar className="w-4 h-4" />
                  {formatDateTime(session.scheduledDate)}
                </div>
                <div className="flex items-center gap-2 text-sm text-gray-600">
                  <Clock className="w-4 h-4" />
                  {session.duration} minutes
                </div>
                <div className="flex items-center gap-2 text-sm text-gray-600">
                  <MapPin className="w-4 h-4" />
                  {session.location?.type === 'online' ? 'Online' : 'In-person'}
                </div>
                <div className="flex items-center gap-2 text-sm text-gray-600">
                  <Users className="w-4 h-4" />
                  {session.participants?.length || 0} participants
                </div>
              </div>

              <div className="flex gap-2">
                {session.isOrganizer ? (
                  <button className="flex-1 btn-secondary text-sm">
                    Manage
                  </button>
                ) : (
                  <button
                    onClick={() => handleJoinSession(session.id)}
                    className="flex-1 btn-primary text-sm"
                    disabled={joinSessionMutation.isLoading}
                  >
                    {joinSessionMutation.isLoading ? 'Joining...' : 'Join'}
                  </button>
                )}
                <button className="px-3 py-2 border border-gray-300 rounded-lg hover:bg-gray-50">
                  <Users className="w-4 h-4" />
                </button>
              </div>
            </div>
          ))}
        </div>
      )}

      {/* Create Session Modal */}
      {showCreateForm && (
        <CreateSessionModal
          onClose={() => setShowCreateForm(false)}
          onSubmit={(data) => createSessionMutation.mutate(data)}
          isLoading={createSessionMutation.isLoading}
        />
      )}
    </div>
  );
};

const CreateSessionModal = ({ onClose, onSubmit, isLoading }) => {
  const [formData, setFormData] = useState({
    title: '',
    subject: '',
    description: '',
    scheduledDate: '',
    duration: 60,
    location: { type: 'online', details: {} }
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit({
      ...formData,
      scheduledDate: new Date(formData.scheduledDate).toISOString()
    });
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white p-6 rounded-xl max-w-md w-full mx-4 max-h-[90vh] overflow-y-auto">
        <h3 className="text-lg font-semibold mb-4">Create Study Session</h3>

        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">Title</label>
            <input
              type="text"
              value={formData.title}
              onChange={(e) => setFormData(prev => ({ ...prev, title: e.target.value }))}
              className="input-primary"
              placeholder="e.g., Calculus Study Group"
              required
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">Subject</label>
            <input
              type="text"
              value={formData.subject}
              onChange={(e) => setFormData(prev => ({ ...prev, subject: e.target.value }))}
              className="input-primary"
              placeholder="e.g., Mathematics"
              required
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">Description</label>
            <textarea
              value={formData.description}
              onChange={(e) => setFormData(prev => ({ ...prev, description: e.target.value }))}
              className="input-primary"
              rows={3}
              placeholder="What will you be studying?"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">Date & Time</label>
            <input
              type="datetime-local"
              value={formData.scheduledDate}
              onChange={(e) => setFormData(prev => ({ ...prev, scheduledDate: e.target.value }))}
              className="input-primary"
              min={new Date().toISOString().slice(0, 16)}
              required
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">Duration (minutes)</label>
            <select
              value={formData.duration}
              onChange={(e) => setFormData(prev => ({ ...prev, duration: parseInt(e.target.value) }))}
              className="input-primary"
            >
              <option value={30}>30 minutes</option>
              <option value={60}>1 hour</option>
              <option value={90}>1.5 hours</option>
              <option value={120}>2 hours</option>
            </select>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">Location</label>
            <select
              value={formData.location.type}
              onChange={(e) => setFormData(prev => ({ 
                ...prev, 
                location: { type: e.target.value, details: {} }
              }))}
              className="input-primary"
            >
              <option value="online">Online</option>
              <option value="in-person">In-person</option>
            </select>
          </div>

          <div className="flex gap-3 pt-4">
            <button
              type="button"
              onClick={onClose}
              className="flex-1 btn-secondary"
            >
              Cancel
            </button>
            <button
              type="submit"
              disabled={isLoading}
              className="flex-1 btn-primary flex items-center justify-center gap-2"
            >
              {isLoading ? (
                <>
                  <LoadingSpinner size="sm" />
                  Creating...
                </>
              ) : (
                'Create Session'
              )}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default SessionsPage;
