import React from 'react';
import { useAuth } from '../contexts/AuthContext';
import { Users, BookOpen, Calendar, MessageCircle, Settings, LogOut } from 'lucide-react';

const Layout = ({ children, currentPage, setCurrentPage }) => {
  const { user, logout } = useAuth();

  const navigation = [
    { id: 'dashboard', label: 'Dashboard', icon: BookOpen },
    { id: 'matches', label: 'Matches', icon: Users },
    { id: 'sessions', label: 'Sessions', icon: Calendar },
    { id: 'messages', label: 'Messages', icon: MessageCircle },
  ];

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Navigation */}
      <nav className="bg-white shadow-sm border-b">
        <div className="max-w-7xl mx-auto px-4">
          <div className="flex items-center justify-between h-16">
            <div className="flex items-center gap-8">
              <div className="flex items-center gap-2">
                <Users className="w-8 h-8 text-primary-600" />
                <span className="text-xl font-bold text-gray-900">StudyBuddy</span>
              </div>

              <div className="flex gap-1">
                {navigation.map(({ id, label, icon: Icon }) => (
                  <button
                    key={id}
                    onClick={() => setCurrentPage(id)}
                    className={`px-4 py-2 rounded-lg flex items-center gap-2 transition-colors ${
                      currentPage === id
                        ? 'bg-primary-100 text-primary-700'
                        : 'text-gray-600 hover:text-gray-900 hover:bg-gray-100'
                    }`}
                  >
                    <Icon className="w-4 h-4" />
                    {label}
                  </button>
                ))}
              </div>
            </div>

            <div className="flex items-center gap-4">
              <button
                onClick={() => setCurrentPage('profile')}
                className="flex items-center gap-2 p-2 rounded-lg hover:bg-gray-100 transition-colors"
              >
                <img src={user?.avatar} alt="Profile" className="w-8 h-8 rounded-full" />
                <span className="text-sm font-medium text-gray-700">{user?.name}</span>
              </button>
              <button
                onClick={logout}
                className="p-2 text-gray-500 hover:text-gray-700 transition-colors"
              >
                <LogOut className="w-5 h-5" />
              </button>
            </div>
          </div>
        </div>
      </nav>

      {/* Main Content */}
      <main className="max-w-7xl mx-auto px-4 py-8">
        {children}
      </main>
    </div>
  );
};

export default Layout;
